@import 'tailwindcss';

@custom-variant dark (&:is(.dark *));

/* BoldKit Theme Definition for Tailwind v4 */
@theme {
  --color-background: hsl(var(--background));
  --color-foreground: hsl(var(--foreground));
  --color-card: hsl(var(--card));
  --color-card-foreground: hsl(var(--card-foreground));
  --color-popover: hsl(var(--popover));
  --color-popover-foreground: hsl(var(--popover-foreground));
  --color-primary: hsl(var(--primary));
  --color-primary-foreground: hsl(var(--primary-foreground));
  --color-secondary: hsl(var(--secondary));
  --color-secondary-foreground: hsl(var(--secondary-foreground));
  --color-accent: hsl(var(--accent));
  --color-accent-foreground: hsl(var(--accent-foreground));
  --color-muted: hsl(var(--muted));
  --color-muted-foreground: hsl(var(--muted-foreground));
  --color-destructive: hsl(var(--destructive));
  --color-destructive-foreground: hsl(var(--destructive-foreground));
  --color-success: hsl(var(--success));
  --color-success-foreground: hsl(var(--success-foreground));
  --color-warning: hsl(var(--warning));
  --color-warning-foreground: hsl(var(--warning-foreground));
  --color-info: hsl(var(--info));
  --color-info-foreground: hsl(var(--info-foreground));
  --color-border: hsl(var(--border));
  --color-input: hsl(var(--input));
  --color-ring: hsl(var(--ring));
  --color-chart-1: hsl(var(--chart-1));
  --color-chart-2: hsl(var(--chart-2));
  --color-chart-3: hsl(var(--chart-3));
  --color-chart-4: hsl(var(--chart-4));
  --color-chart-5: hsl(var(--chart-5));
  /* Neon Colors */
  --color-neon-pink: hsl(var(--neon-pink));
  --color-neon-green: hsl(var(--neon-green));
  --color-neon-blue: hsl(var(--neon-blue));
  --color-neon-orange: hsl(var(--neon-orange));
  --color-neon-purple: hsl(var(--neon-purple));
  /* Clash Colors */
  --color-clash-1: hsl(var(--clash-1));
  --color-clash-2: hsl(var(--clash-2));
  --color-clash-3: hsl(var(--clash-3));
  --color-clash-4: hsl(var(--clash-4));
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
}

/* BoldKit - Neubrutalism Design System */
:root {
  /* Base Colors */
  --background: 60 9% 98%;
  --foreground: 240 10% 10%;

  /* Card */
  --card: 0 0% 100%;
  --card-foreground: 240 10% 10%;

  /* Popover */
  --popover: 0 0% 100%;
  --popover-foreground: 240 10% 10%;

  /* Primary - Coral Red */
  --primary: 0 84% 71%;
  --primary-foreground: 240 10% 10%;

  /* Secondary - Teal */
  --secondary: 174 62% 56%;
  --secondary-foreground: 240 10% 10%;

  /* Accent - Yellow */
  --accent: 49 100% 71%;
  --accent-foreground: 240 10% 10%;

  /* Muted */
  --muted: 60 5% 90%;
  --muted-foreground: 240 4% 46%;

  /* Destructive */
  --destructive: 0 84% 60%;
  --destructive-foreground: 0 0% 100%;

  /* Success */
  --success: 152 69% 69%;
  --success-foreground: 240 10% 10%;

  /* Warning */
  --warning: 49 100% 60%;
  --warning-foreground: 240 10% 10%;

  /* Info */
  --info: 212 100% 73%;
  --info-foreground: 240 10% 10%;

  /* Border & Input */
  --border: 240 10% 10%;
  --input: 240 10% 10%;
  --ring: 240 10% 10%;

  /* Radius - Minimal for neubrutalism */
  --radius: 0rem;

  /* BoldKit specific */
  --shadow-color: 240 10% 10%;
  --shadow-offset: 4px;
  --border-width: 3px;

  /* Chart colors */
  --chart-1: 0 84% 71%;
  --chart-2: 174 62% 56%;
  --chart-3: 49 100% 71%;
  --chart-4: 271 76% 53%;
  --chart-5: 326 78% 60%;

  /* Neon Colors - High saturation, vibrant */
  --neon-pink: 330 100% 65%;
  --neon-green: 120 100% 50%;
  --neon-blue: 195 100% 50%;
  --neon-orange: 25 100% 55%;
  --neon-purple: 280 100% 60%;

  /* Clash Colors - Unconventional pairings */
  --clash-1: 15 85% 60%;   /* Burnt orange */
  --clash-2: 280 70% 50%;  /* Deep purple */
  --clash-3: 165 80% 45%;  /* Cyan-teal */
  --clash-4: 45 95% 55%;   /* Mustard */
}

.dark {
  --background: 240 10% 10%;
  --foreground: 60 9% 98%;

  --card: 240 10% 14%;
  --card-foreground: 60 9% 98%;

  --popover: 240 10% 14%;
  --popover-foreground: 60 9% 98%;

  --primary: 0 84% 71%;
  --primary-foreground: 240 10% 10%;

  --secondary: 174 62% 56%;
  --secondary-foreground: 240 10% 10%;

  --accent: 49 100% 71%;
  --accent-foreground: 240 10% 10%;

  --muted: 240 10% 20%;
  --muted-foreground: 60 5% 65%;

  --destructive: 0 84% 60%;
  --destructive-foreground: 0 0% 100%;

  --success: 152 69% 69%;
  --success-foreground: 240 10% 10%;

  --warning: 49 100% 60%;
  --warning-foreground: 240 10% 10%;

  --info: 212 100% 73%;
  --info-foreground: 240 10% 10%;

  --border: 60 9% 98%;
  --input: 60 9% 98%;
  --ring: 60 9% 98%;

  --shadow-color: 0 0% 0%;

  /* Neon Colors - Slightly adjusted for dark mode */
  --neon-pink: 330 100% 70%;
  --neon-green: 120 100% 55%;
  --neon-blue: 195 100% 55%;
  --neon-orange: 25 100% 60%;
  --neon-purple: 280 100% 65%;

  /* Clash Colors */
  --clash-1: 15 85% 65%;
  --clash-2: 280 70% 55%;
  --clash-3: 165 80% 50%;
  --clash-4: 45 95% 60%;
}

/* Base styles */
* {
  @apply border-border;
}

body {
  @apply bg-background text-foreground antialiased;
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
}

/* BoldKit Shadow Utility */
.bk-shadow {
  box-shadow: var(--shadow-offset) var(--shadow-offset) 0px hsl(var(--shadow-color));
}

.bk-shadow-sm {
  box-shadow: 2px 2px 0px hsl(var(--shadow-color));
}

.bk-shadow-md {
  box-shadow: 6px 6px 0px hsl(var(--shadow-color));
}

.bk-shadow-lg {
  box-shadow: 8px 8px 0px hsl(var(--shadow-color));
}

.bk-shadow-xl {
  box-shadow: 12px 12px 0px hsl(var(--shadow-color));
}

.bk-shadow-2xl {
  box-shadow: 16px 16px 0px hsl(var(--shadow-color));
}

/* Colored Shadow Variants */
.bk-shadow-primary {
  box-shadow: 4px 4px 0px hsl(var(--primary));
}

.bk-shadow-secondary {
  box-shadow: 4px 4px 0px hsl(var(--secondary));
}

.bk-shadow-accent {
  box-shadow: 4px 4px 0px hsl(var(--accent));
}

.bk-shadow-destructive {
  box-shadow: 4px 4px 0px hsl(var(--destructive));
}

.bk-shadow-neon-pink {
  box-shadow: 4px 4px 0px hsl(var(--neon-pink));
}

.bk-shadow-neon-green {
  box-shadow: 4px 4px 0px hsl(var(--neon-green));
}

.bk-shadow-neon-blue {
  box-shadow: 4px 4px 0px hsl(var(--neon-blue));
}

/* Double Shadow Effect - Layered depth */
.bk-shadow-double {
  box-shadow:
    4px 4px 0px hsl(var(--primary)),
    8px 8px 0px hsl(var(--shadow-color));
}

.bk-shadow-double-accent {
  box-shadow:
    4px 4px 0px hsl(var(--accent)),
    8px 8px 0px hsl(var(--shadow-color));
}

.bk-shadow-double-lg {
  box-shadow:
    6px 6px 0px hsl(var(--primary)),
    12px 12px 0px hsl(var(--shadow-color));
}

/* Triple Shadow Effect - Maximum depth */
.bk-shadow-triple {
  box-shadow:
    3px 3px 0px hsl(var(--primary)),
    6px 6px 0px hsl(var(--secondary)),
    9px 9px 0px hsl(var(--shadow-color));
}

/* Hover transform effect */
.bk-hover:hover {
  transform: translate(-2px, -2px);
}

.bk-hover:active {
  transform: translate(2px, 2px);
  box-shadow: 2px 2px 0px hsl(var(--shadow-color));
}

/* Enhanced hover with scale */
.bk-hover-scale:hover {
  transform: translate(-2px, -2px) scale(1.02);
}

.bk-hover-scale:active {
  transform: translate(2px, 2px) scale(0.98);
  box-shadow: 2px 2px 0px hsl(var(--shadow-color));
}

/* Border Variations */
.border-4 {
  border-width: 4px;
}

.border-5 {
  border-width: 5px;
}

.border-6 {
  border-width: 6px;
}

/* Double Border Effect - Sticker/Stamp style */
.bk-double-border {
  outline: 3px solid hsl(var(--foreground));
  outline-offset: 3px;
}

.bk-double-border-primary {
  outline: 3px solid hsl(var(--primary));
  outline-offset: 3px;
}

.bk-double-border-accent {
  outline: 3px solid hsl(var(--accent));
  outline-offset: 3px;
}

/* Dashed border for rough/hand-drawn feel */
.bk-dashed-border {
  border-style: dashed;
  border-width: 3px;
}

/* Sticker Effect - Rotated with double border */
.bk-sticker {
  position: relative;
  display: inline-block;
  padding: 0.5rem 1rem;
  border: 3px solid hsl(var(--foreground));
  background: hsl(var(--accent));
  box-shadow: 4px 4px 0px hsl(var(--shadow-color));
  transform: rotate(-2deg);
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.bk-sticker::before {
  content: '';
  position: absolute;
  inset: -6px;
  border: 2px dashed hsl(var(--foreground) / 0.5);
}

.bk-sticker-tilted {
  transform: rotate(-6deg);
}

.bk-sticker-tilted-right {
  transform: rotate(6deg);
}

/* Stamp Effect - Circular with rough edges */
.bk-stamp {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding: 1rem;
  border: 4px solid hsl(var(--foreground));
  border-radius: 50%;
  background: hsl(var(--primary));
  color: hsl(var(--primary-foreground));
  font-weight: 900;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  box-shadow: 4px 4px 0px hsl(var(--shadow-color));
  transform: rotate(-12deg);
}

/* Tape Effect - Decorative tape strips */
.bk-tape {
  position: relative;
}

.bk-tape::before {
  content: '';
  position: absolute;
  top: -8px;
  left: 50%;
  transform: translateX(-50%) rotate(-2deg);
  width: 60px;
  height: 20px;
  background: hsl(var(--accent) / 0.8);
  border: 2px solid hsl(var(--foreground));
}

/* Selection */
::selection {
  @apply bg-accent text-accent-foreground;
}

/* ==========================================
   TYPOGRAPHY ENHANCEMENTS
   ========================================== */

/* Extra-wide letter spacing for emphasis */
.tracking-extra-wide {
  letter-spacing: 0.2em;
}

.tracking-ultra-wide {
  letter-spacing: 0.3em;
}

/* Text overflow effect - intentional overflow */
.bk-text-overflow {
  overflow: visible;
  white-space: nowrap;
}

.bk-text-overflow-clip {
  position: relative;
}

.bk-text-overflow-clip::after {
  content: '';
  position: absolute;
  right: -20px;
  top: 0;
  bottom: 0;
  width: 40px;
  background: linear-gradient(to right, transparent, hsl(var(--background)));
}

/* Dramatic text sizes */
.text-display {
  font-size: 4rem;
  line-height: 1;
  font-weight: 900;
  letter-spacing: -0.02em;
}

.text-display-lg {
  font-size: 6rem;
  line-height: 0.9;
  font-weight: 900;
  letter-spacing: -0.03em;
}

.text-display-xl {
  font-size: 8rem;
  line-height: 0.85;
  font-weight: 900;
  letter-spacing: -0.04em;
}

/* Outlined text */
.bk-text-outline {
  -webkit-text-stroke: 2px hsl(var(--foreground));
  color: transparent;
}

.bk-text-outline-thick {
  -webkit-text-stroke: 3px hsl(var(--foreground));
  color: transparent;
}

/* Text with shadow */
.bk-text-shadow {
  text-shadow: 3px 3px 0px hsl(var(--shadow-color));
}

.bk-text-shadow-lg {
  text-shadow: 5px 5px 0px hsl(var(--shadow-color));
}

.bk-text-shadow-colored {
  text-shadow: 3px 3px 0px hsl(var(--primary));
}

/* ==========================================
   TEXTURE & PATTERN UTILITIES
   ========================================== */

/* Noise/Grain texture overlay */
.bk-noise {
  position: relative;
}

.bk-noise::before {
  content: '';
  position: absolute;
  inset: 0;
  background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 256 256' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noise'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.8' numOctaves='4' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noise)'/%3E%3C/svg%3E");
  opacity: 0.08;
  pointer-events: none;
  mix-blend-mode: overlay;
}

.bk-noise-heavy::before {
  opacity: 0.15;
}

.bk-noise-light::before {
  opacity: 0.04;
}

/* Halftone dot pattern */
.bk-halftone {
  position: relative;
}

.bk-halftone::before {
  content: '';
  position: absolute;
  inset: 0;
  background-image: radial-gradient(circle, hsl(var(--foreground) / 0.15) 1px, transparent 1px);
  background-size: 8px 8px;
  pointer-events: none;
}

.bk-halftone-dense::before {
  background-size: 4px 4px;
}

.bk-halftone-sparse::before {
  background-size: 12px 12px;
}

/* Grid pattern variations */
.grid-pattern-sm {
  background-image:
    linear-gradient(hsl(var(--border) / 0.3) 1px, transparent 1px),
    linear-gradient(90deg, hsl(var(--border) / 0.3) 1px, transparent 1px);
  background-size: 20px 20px;
}

.grid-pattern-lg {
  background-image:
    linear-gradient(hsl(var(--border) / 0.3) 1px, transparent 1px),
    linear-gradient(90deg, hsl(var(--border) / 0.3) 1px, transparent 1px);
  background-size: 60px 60px;
}

.grid-pattern-bold {
  background-image:
    linear-gradient(hsl(var(--border) / 0.5) 2px, transparent 2px),
    linear-gradient(90deg, hsl(var(--border) / 0.5) 2px, transparent 2px);
  background-size: 40px 40px;
}

/* Diagonal lines pattern */
.bk-diagonal-lines {
  background-image: repeating-linear-gradient(
    45deg,
    transparent,
    transparent 10px,
    hsl(var(--border) / 0.2) 10px,
    hsl(var(--border) / 0.2) 11px
  );
}

/* Checkerboard pattern */
.bk-checkerboard {
  background-image:
    linear-gradient(45deg, hsl(var(--muted)) 25%, transparent 25%),
    linear-gradient(-45deg, hsl(var(--muted)) 25%, transparent 25%),
    linear-gradient(45deg, transparent 75%, hsl(var(--muted)) 75%),
    linear-gradient(-45deg, transparent 75%, hsl(var(--muted)) 75%);
  background-size: 20px 20px;
  background-position: 0 0, 0 10px, 10px -10px, -10px 0px;
}

/* Focus visible */
:focus-visible {
  @apply outline-none ring-2 ring-ring ring-offset-2 ring-offset-background;
}

/* Scrollbar styling */
::-webkit-scrollbar {
  width: 12px;
  height: 12px;
}

::-webkit-scrollbar-track {
  @apply bg-muted;
}

::-webkit-scrollbar-thumb {
  @apply bg-foreground border-3 border-muted;
}

/* Grid pattern for hero sections */
.grid-pattern {
  background-image:
    linear-gradient(hsl(var(--border) / 0.3) 1px, transparent 1px),
    linear-gradient(90deg, hsl(var(--border) / 0.3) 1px, transparent 1px);
  background-size: 40px 40px;
}

/* Animation Keyframes */
@keyframes accordion-down {
  from {
    height: 0;
    opacity: 0;
  }
  to {
    height: var(--reka-accordion-content-height, var(--radix-accordion-content-height));
    opacity: 1;
  }
}

@keyframes accordion-up {
  from {
    height: var(--reka-accordion-content-height, var(--radix-accordion-content-height));
    opacity: 1;
  }
  to {
    height: 0;
    opacity: 0;
  }
}

@keyframes collapsible-down {
  from {
    height: 0;
    opacity: 0;
  }
  to {
    height: var(--reka-collapsible-content-height, var(--radix-collapsible-content-height));
    opacity: 1;
  }
}

@keyframes collapsible-up {
  from {
    height: var(--reka-collapsible-content-height, var(--radix-collapsible-content-height));
    opacity: 1;
  }
  to {
    height: 0;
    opacity: 0;
  }
}

@keyframes shake {
  0%, 100% { transform: translateX(0); }
  25% { transform: translateX(-4px); }
  75% { transform: translateX(4px); }
}

@keyframes bounce-in {
  0% { transform: scale(0.95); opacity: 0; }
  50% { transform: scale(1.02); }
  100% { transform: scale(1); opacity: 1; }
}

@keyframes pulse-shadow {
  0%, 100% { box-shadow: 4px 4px 0px hsl(var(--shadow-color)); }
  50% { box-shadow: 6px 6px 0px hsl(var(--shadow-color)); }
}

/* Wiggle animation - playful error state */
@keyframes wiggle {
  0%, 100% { transform: rotate(0deg); }
  25% { transform: rotate(-3deg); }
  50% { transform: rotate(3deg); }
  75% { transform: rotate(-3deg); }
}

/* Pop animation - success/attention */
@keyframes pop {
  0% { transform: scale(1); }
  50% { transform: scale(1.1); }
  100% { transform: scale(1); }
}

/* Rubber band effect */
@keyframes rubber-band {
  0% { transform: scale(1); }
  30% { transform: scaleX(1.25) scaleY(0.75); }
  40% { transform: scaleX(0.75) scaleY(1.25); }
  50% { transform: scaleX(1.15) scaleY(0.85); }
  65% { transform: scaleX(0.95) scaleY(1.05); }
  75% { transform: scaleX(1.05) scaleY(0.95); }
  100% { transform: scale(1); }
}

/* Jello wobble */
@keyframes jello {
  0%, 100% { transform: skewX(0deg) skewY(0deg); }
  30% { transform: skewX(-12.5deg) skewY(-12.5deg); }
  40% { transform: skewX(6.25deg) skewY(6.25deg); }
  50% { transform: skewX(-3.125deg) skewY(-3.125deg); }
  65% { transform: skewX(1.5625deg) skewY(1.5625deg); }
  75% { transform: skewX(-0.78125deg) skewY(-0.78125deg); }
}

/* Marquee scroll animation */
@keyframes marquee {
  0% { transform: translateX(0); }
  100% { transform: translateX(-50%); }
}

@keyframes marquee-reverse {
  0% { transform: translateX(-50%); }
  100% { transform: translateX(0); }
}

/* Stagger fade in */
@keyframes stagger-fade-in {
  0% { opacity: 0; transform: translateY(10px); }
  100% { opacity: 1; transform: translateY(0); }
}

/* Pulse scale */
@keyframes pulse-scale {
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.05); }
}

/* Glitch effect */
@keyframes glitch {
  0%, 100% { transform: translate(0); }
  20% { transform: translate(-2px, 2px); }
  40% { transform: translate(-2px, -2px); }
  60% { transform: translate(2px, 2px); }
  80% { transform: translate(2px, -2px); }
}

/* Float animation */
@keyframes float {
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-10px); }
}

/* Typewriter cursor blink */
@keyframes blink {
  0%, 50% { opacity: 1; }
  51%, 100% { opacity: 0; }
}

/* Animation Classes */
.animate-accordion-down {
  animation: accordion-down 200ms ease-out;
}

.animate-accordion-up {
  animation: accordion-up 200ms ease-out;
}

.animate-collapsible-down {
  animation: collapsible-down 200ms ease-out;
}

.animate-collapsible-up {
  animation: collapsible-up 200ms ease-out;
}

.animate-shake {
  animation: shake 200ms ease-in-out;
}

.animate-bounce-in {
  animation: bounce-in 300ms ease-out;
}

.animate-pulse-shadow {
  animation: pulse-shadow 2s ease-in-out infinite;
}

.animate-wiggle {
  animation: wiggle 300ms ease-in-out;
}

.animate-pop {
  animation: pop 300ms ease-out;
}

.animate-rubber-band {
  animation: rubber-band 800ms ease-out;
}

.animate-jello {
  animation: jello 800ms ease-out;
}

.animate-marquee {
  animation: marquee 20s linear infinite;
}

.animate-marquee-fast {
  animation: marquee 10s linear infinite;
}

.animate-marquee-slow {
  animation: marquee 40s linear infinite;
}

.animate-marquee-reverse {
  animation: marquee-reverse 20s linear infinite;
}

.animate-stagger-fade-in {
  animation: stagger-fade-in 300ms ease-out forwards;
  opacity: 0;
}

/* Stagger delay utilities */
.stagger-1 { animation-delay: 50ms; }
.stagger-2 { animation-delay: 100ms; }
.stagger-3 { animation-delay: 150ms; }
.stagger-4 { animation-delay: 200ms; }
.stagger-5 { animation-delay: 250ms; }
.stagger-6 { animation-delay: 300ms; }
.stagger-7 { animation-delay: 350ms; }
.stagger-8 { animation-delay: 400ms; }

.animate-pulse-scale {
  animation: pulse-scale 2s ease-in-out infinite;
}

.animate-glitch {
  animation: glitch 200ms ease-in-out;
}

.animate-float {
  animation: float 3s ease-in-out infinite;
}

.animate-blink {
  animation: blink 1s step-end infinite;
}

/* Hover-triggered animations */
.hover-wiggle:hover {
  animation: wiggle 300ms ease-in-out;
}

.hover-pop:hover {
  animation: pop 300ms ease-out;
}

.hover-jello:hover {
  animation: jello 800ms ease-out;
}

.hover-rubber-band:hover {
  animation: rubber-band 800ms ease-out;
}

/* Tooltip & Popover Animations */
@keyframes fade-in {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes fade-out {
  from { opacity: 1; }
  to { opacity: 0; }
}

@keyframes zoom-in {
  from { transform: scale(0.95); opacity: 0; }
  to { transform: scale(1); opacity: 1; }
}

@keyframes zoom-out {
  from { transform: scale(1); opacity: 1; }
  to { transform: scale(0.95); opacity: 0; }
}

@keyframes slide-in-from-top {
  from { transform: translateY(-4px); }
  to { transform: translateY(0); }
}

@keyframes slide-in-from-bottom {
  from { transform: translateY(4px); }
  to { transform: translateY(0); }
}

@keyframes slide-in-from-left {
  from { transform: translateX(-4px); }
  to { transform: translateX(0); }
}

@keyframes slide-in-from-right {
  from { transform: translateX(4px); }
  to { transform: translateX(0); }
}

/* Reka UI animation states (compatible with Radix) */
[data-state="open"] {
  animation: zoom-in 150ms ease-out;
}

[data-state="closed"] {
  animation: zoom-out 100ms ease-in;
}

/* Tooltip specific animations */
[data-reka-popper-content-wrapper] [data-side="top"],
[data-radix-popper-content-wrapper] [data-side="top"] {
  animation: fade-in 150ms ease-out, slide-in-from-bottom 150ms ease-out;
}

[data-reka-popper-content-wrapper] [data-side="bottom"],
[data-radix-popper-content-wrapper] [data-side="bottom"] {
  animation: fade-in 150ms ease-out, slide-in-from-top 150ms ease-out;
}

[data-reka-popper-content-wrapper] [data-side="left"],
[data-radix-popper-content-wrapper] [data-side="left"] {
  animation: fade-in 150ms ease-out, slide-in-from-right 150ms ease-out;
}

[data-reka-popper-content-wrapper] [data-side="right"],
[data-radix-popper-content-wrapper] [data-side="right"] {
  animation: fade-in 150ms ease-out, slide-in-from-left 150ms ease-out;
}

/* ==========================================
   MARQUEE COMPONENT UTILITIES
   ========================================== */

.bk-marquee {
  display: flex;
  overflow: hidden;
  user-select: none;
  gap: 1rem;
}

.bk-marquee-content {
  display: flex;
  gap: 1rem;
  flex-shrink: 0;
  animation: marquee 20s linear infinite;
}

.bk-marquee:hover .bk-marquee-content {
  animation-play-state: paused;
}

.bk-marquee-vertical {
  flex-direction: column;
  height: 100%;
}

.bk-marquee-vertical .bk-marquee-content {
  flex-direction: column;
  animation: marquee-vertical 20s linear infinite;
}

@keyframes marquee-vertical {
  0% { transform: translateY(0); }
  100% { transform: translateY(-50%); }
}

/* ==========================================
   LAYERED CARD UTILITIES
   ========================================== */

.bk-layered {
  position: relative;
}

.bk-layered::before,
.bk-layered::after {
  content: '';
  position: absolute;
  inset: 0;
  border: 3px solid hsl(var(--foreground));
  background: hsl(var(--card));
  z-index: -1;
}

.bk-layered::before {
  transform: translate(6px, 6px);
}

.bk-layered::after {
  transform: translate(12px, 12px);
}

.bk-layered-sm::before {
  transform: translate(4px, 4px);
}

.bk-layered-sm::after {
  transform: translate(8px, 8px);
}

.bk-layered-lg::before {
  transform: translate(8px, 8px);
}

.bk-layered-lg::after {
  transform: translate(16px, 16px);
}

/* Single layer variant */
.bk-layered-single::after {
  display: none;
}

/* Colored layers */
.bk-layered-primary::before {
  background: hsl(var(--primary));
}

.bk-layered-accent::before {
  background: hsl(var(--accent));
}

.bk-layered-secondary::before {
  background: hsl(var(--secondary));
}

/* ==========================================
   NEON GLOW UTILITIES
   ========================================== */

.bk-glow-neon-pink {
  box-shadow:
    0 0 5px hsl(var(--neon-pink)),
    0 0 10px hsl(var(--neon-pink)),
    0 0 20px hsl(var(--neon-pink) / 0.5);
}

.bk-glow-neon-green {
  box-shadow:
    0 0 5px hsl(var(--neon-green)),
    0 0 10px hsl(var(--neon-green)),
    0 0 20px hsl(var(--neon-green) / 0.5);
}

.bk-glow-neon-blue {
  box-shadow:
    0 0 5px hsl(var(--neon-blue)),
    0 0 10px hsl(var(--neon-blue)),
    0 0 20px hsl(var(--neon-blue) / 0.5);
}

/* Combined neon + brutal shadow */
.bk-shadow-neon-brutal-pink {
  box-shadow:
    4px 4px 0px hsl(var(--shadow-color)),
    0 0 10px hsl(var(--neon-pink) / 0.5);
}

.bk-shadow-neon-brutal-green {
  box-shadow:
    4px 4px 0px hsl(var(--shadow-color)),
    0 0 10px hsl(var(--neon-green) / 0.5);
}

/* ==========================================
   UTILITY HELPERS
   ========================================== */

/* Force neubrutalism style reset */
.bk-brutal {
  border-radius: 0 !important;
  border-width: 3px;
  border-style: solid;
  border-color: hsl(var(--foreground));
}

/* Pressed state utility */
.bk-pressed {
  transform: translate(4px, 4px);
  box-shadow: none !important;
}

/* Lifted state utility */
.bk-lifted {
  transform: translate(-4px, -4px);
  box-shadow: 8px 8px 0px hsl(var(--shadow-color));
}

/* ==========================================
   BUTTON ANIMATION KEYFRAMES
   ========================================== */

@keyframes brutal-pulse {
  0%, 100% {
    box-shadow: 4px 4px 0px hsl(var(--shadow-color));
    transform: translate(0, 0);
  }
  50% {
    box-shadow: 6px 6px 0px hsl(var(--shadow-color));
    transform: translate(-2px, -2px);
  }
}

@keyframes brutal-bounce {
  0%, 100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-6px);
  }
}

@keyframes brutal-shake {
  0%, 100% {
    transform: translateX(0);
  }
  20% {
    transform: translateX(-4px) rotate(-2deg);
  }
  40% {
    transform: translateX(4px) rotate(2deg);
  }
  60% {
    transform: translateX(-4px) rotate(-2deg);
  }
  80% {
    transform: translateX(4px) rotate(2deg);
  }
}

@keyframes brutal-wiggle {
  0%, 100% {
    transform: rotate(0deg);
  }
  25% {
    transform: rotate(-5deg);
  }
  75% {
    transform: rotate(5deg);
  }
}

@keyframes brutal-pop {
  0% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.1);
  }
  100% {
    transform: scale(1);
  }
}
